sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var rn = g_form.getReference('student',callBack);

}
function callBack(usr){

	g_form.setValue('student_rollno',usr.student_rollno);
	g_form.setValue('physics',usr.physics);
	g_form.setValue('chemistry',usr.chemistry);
	g_form.setValue('mathematics',usr.mathematics);
	g_form.setValue('u_integer_8',usr.computer_science);
	g_form.setValue('student_email',usr.student_email);

}
